#include <bits/stdc++.h> using namespace std;#define ll long long ll gcd(ll a, ll b) {    if (b == 0) return a;    return gcd(b, a % b);} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;         vector<ll> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }         const ll INF = 4e18;        ll ans = 0;        int i = 0;         while (i < n) {            ll rem = 0;            ll low = 0, high = INF;            ll minOdd = 0, minEven = INF;            bool isOdd = true;             i++; // start new segment             while (i < n) {                ll diff = a[i] - a[i - 1];                ll cur = diff - rem;                 bool nextOdd = !isOdd;                ll nlow = low, nhigh = high;                 if (nextOdd) {                    nlow = max(nlow, -cur);                } else {                    nhigh = min(nhigh, cur);                }                 if (nlow >= nhigh) break;                 ll extra = 0;                ll prevMin = nextOdd ? minOdd : minEven;                if (prevMin < INF) {                    // Fixed: Removed space between 0 and LL                    extra = max(0LL, cur - prevMin);                 }                 if (i + 1 < n) {                    ll nextDiff = a[i + 1] - a[i];                    if (extra >= nextDiff) break;                }                 // accept this step                ans++;                rem = cur;                low = nlow;                high = nhigh;                 if (nextOdd)                    minOdd = min(minOdd, cur);                else                    minEven = min(minEven, cur);                 isOdd = nextOdd;                i++;            }        }         cout << ans << '\n';    }     return 0;}